module.exports = {
  playerId : function(id) {
    this.name = 'playerId';
    this.id = id;
  },
  
  createPlayer : function(player) {
    this.name = 'createPlayer';
    this.info = player.attributes();
  },
  
  createLocalPlayer : function(player) {
    this.name = 'createLocalPlayer';
    this.info = player.attributes();
  },
  
  noMorePlayers : function() {
    this.name = 'noMorePlayers';
  },
  
  velocityUpdate : function(id , velocity) {
    this.name     = 'velocityUpdate';
    this.id       = id;
    this.velocity = velocity;
  },
  
  positionUpdate : function(id , position) {
    this.name     = 'positionUpdate';
    this.id       = id;
    this.position = position;
  },
  
  accelerationUpdate : function(id , acceleration) {
    this.name         = 'accelerationUpdate';
    this.id           = id;
    this.acceleration = acceleration;
  },
  
  fetchGroundTile : function(tile) {
    this.name             = 'fetchGroundTile';
    this.attributes       = tile.attributes();
    this.attributes.world = undefined;
  },
  
  noMoreGroundTiles : function() {
    this.name = 'noMoreGroundTiles';
  }
}